// Extrai o conte√∫do da resposta da OpenAI
let conteudo = '';
if (openaiResponse.message?.content) {
  conteudo = openaiResponse.message.content;
} else if (openaiResponse.text) {
  conteudo = openaiResponse.text;
} else if (openaiResponse.content) {
  conteudo = openaiResponse.content;
} else if (typeof openaiResponse === 'string') {
  conteudo = openaiResponse;
} else {
  conteudo = JSON.stringify(openaiResponse, null, 2);
}

// Limpa APENAS links markdown e excesso de linhas
conteudo = conteudo
  .replace(/```json|```/g, '')
  .replace(/\[([^\]]+)\]\([^)]+\)/g, '$1')
  .replace(/\n{4,}/g, '\n\n')
  .trim();

